mapper.sql:
n.applyTime>= #{startRecordDate} AND n.applyTime<=#{endRecordDate}
 
	AND n.channelDetail !='' 
 

	AND n.channelDetail = #{channelDetail}


	AND n.channelCode = #{channelCode}

	n.applyTime>= #{startRecordDate} AND n.applyTime<=#{endRecordDate}
	  
  		 and n.channelDetail like CONCAT(#{channelCode},'%')
      
      
		and FIND_IN_SET(n.channelCode,#{fixChannels})
	  
	  
		AND n.cityName = #{cityName}
	
	  and n.channelDetail not like '%_sale'

mapper.select:
SELECT COUNT(1) FROM (SELECT COUNT(1) FROM t_borrow_apply t LEFT JOIN t_borrow_channel_dtl t1 ON t.channelDetail = t1.detailCode WHERE t.applyTime>= #{startRecordDate} AND t.applyTime<= #{endRecordDate}
AND t.channelDetail !=''
GROUP BY t.channelDetail) tab1

mapper.sql:
n.applyTime>= #{startRecordDate} AND n.applyTime<= #{endRecordDate}
 
	AND n.channelDetail !='' 
 

	AND n.channelCode = #{channelCode}


	AND n.channelDetail = #{channelDetail}

n.isRepeat=0 AND n.createTime>= #{startRecordDate} AND n.createTime<= #{endRecordDate}
 
	AND n.channelDetail !='' 
 

	AND n.sourceChannel = #{channelCode}


	AND n.channelDetail = #{channelDetail}

mapper.select:
SELECT COUNT(1) FROM( SELECT t.channelDetail FROM t_apply t LEFT JOIN t_borrow_channel_dtl t1 ON t1.detailCode = t.channelDetail
t.applyTime>= #{startRecordDate} AND t.applyTime<= #{endRecordDate}
AND t.channelDetail !=''
AND t.channelCode = #{channelCode}
AND t.channelDetail = #{channelDetail}
AND t1.channelName like CONCAT(#{channelName},'%')
GROUP BY t.channelDetail UNION ALL SELECT t.channelDetail as 'channelDetail' FROM t_third_data t LEFT JOIN t_borrow_channel_dtl t1 ON t1.channelCode = t.sourceChannel
t.isRepeat=0 AND t.createTime>= #{startRecordDate} AND t.createTime<= #{endRecordDate}
AND t.sourceChannel !=''
AND t.channelDetail !=''
AND t.sourceChannel = #{channelCode}
AND t1.channelName like CONCAT(#{channelName},'%')
AND t1.teamNo= #{teamNo}
GROUP BY t.channelDetail ) tab1
