mapper.select:
SELECT COUNT(1) FROM( SELECT t.channelCode FROM t_apply t LEFT JOIN t_borrow_channel t1 ON t1.channelCode = t.channelCode
t.applyTime>= #{startRecordDate} AND t.applyTime<= #{endRecordDate}
AND t.channelCode !=''
AND FIND_IN_SET(t.channelCode,#{fixChannels})
AND t.channelCode = #{channelCode}
AND t1.channelName like CONCAT(#{channelName},'%')
AND t1.teamNo= #{teamNo}
GROUP BY t.channelCode UNION ALL SELECT t.sourceChannel as 'channelCode' FROM t_third_data t LEFT JOIN t_borrow_channel t1 ON t1.channelCode = t.sourceChannel
t.createTime>= #{startRecordDate} AND t.createTime<= #{endRecordDate}
AND t.sourceChannel !=''
AND t.sourceChannel = #{channelCode}
AND FIND_IN_SET(t.sourceChannel,#{fixChannels})
AND t1.channelName like CONCAT(#{channelName},'%')
GROUP BY t.sourceChannel ) tab1

SELECT CONCAT_WS('至',#{startDateStr},#{endDateStr}) as 'recordDate', t.cityName, t.applyCount as 'applyCount', IFNULL(t3.netStoreCount,0) as 'netStoreCount', IFNULL(t3.dealStoreCount,0) as 'dealStoreCount', IFNULL(t3.labelCount0,0) as 'labelCount0', IFNULL(t3.labelCount1,0) as 'labelCount1', IFNULL(t3.labelCount2,0) as 'labelCount2', IFNULL(t3.labelCount3, 0) AS 'labelCount3', IFNULL(t3.labelCount4,0) as 'labelCount4', IFNULL(t3.labelCount5,0) as 'labelCount5', CONCAT(ROUND(IFNULL(IFNULL(t3.netStoreCount,0) / t.applyCount * 100,0),2),'%') AS 'nav', CONCAT(ROUND(IFNULL((IFNULL(t3.labelCount3,0)+IFNULL(t3.labelCount4,0)+IFNULL(t3.labelCount5,0)) / IFNULL(t3.dealStoreCount,0) * 100,0),2),'%') AS 'adv', CONCAT(ROUND(IFNULL(((IFNULL(t3.labelCount4,0)+IFNULL(t3.labelCount5,0)) / IFNULL(t3.dealStoreCount,0) * 100),0),2),'%') AS 'adv2', CONCAT(ROUND(IFNULL((IFNULL(t3.labelCount3,0)+IFNULL(t3.labelCount4,0)+IFNULL(t3.labelCount5,0)) / t.applyCount * 100,0),2),'%') AS 'lav', CONCAT(ROUND(IFNULL((IFNULL(t3.labelCount4,0)+IFNULL(t3.labelCount5,0)) / t.applyCount * 100,0),2),'%') AS 'lav2', CONCAT(ROUND(IFNULL(IFNULL(t3.dealStoreCount,0) / IFNULL(t3.netStoreCount,0) * 100,0),2),'%') AS 'dnv' FROM ( SELECT n.cityName, COUNT(n.applyId) as 'applyCount' FROM t_borrow_apply n WHERE n.applyTime>= #{startRecordDate} AND n.applyTime<=#{endRecordDate} AND FIND_IN_SET(n.cityName,(SELECT allotCitys FROM t_base_cfg))
AND n.cityName = #{cityName}
GROUP BY n.cityName ) as t LEFT JOIN ( SELECT n.cityName, COUNT(n.applyId) as 'netStoreCount', COUNT(CASE WHEN n.orderStatus !=-1 OR n.orderType =2 THEN 1 END) as 'dealStoreCount', COUNT(CASE WHEN n.custLabel=0 THEN 1 END) as 'labelCount0', COUNT(CASE WHEN n.custLabel=1 THEN 1 END) as 'labelCount1', COUNT(CASE WHEN n.custLabel=2 THEN 1 END) as 'labelCount2', COUNT(CASE WHEN n.custLabel=3 THEN 1 END) as 'labelCount3', COUNT(CASE WHEN n.custLabel=4 THEN 1 END) as 'labelCount4', COUNT(CASE WHEN n.custLabel=5 THEN 1 END) as 'labelCount5' FROM t_borrow_store_apply n
WHERE n.applyTime>= #{startRecordDate} AND n.applyTime<=#{endRecordDate} AND FIND_IN_SET(n.cityName,(SELECT allotCitys FROM t_base_cfg))
AND n.cityName = #{cityName}
GROUP BY n.cityName ) t3 ON t3.cityName = t.cityName

SELECT COUNT(*) FROM ( SELECT n.cityName, COUNT(n.applyId) as 'applyCount', COUNT(CASE WHEN n.applyType=1 OR n.applyType =6 THEN 1 END) as 'seniorCount', DATE_FORMAT(n.applyTime,#{datePattern}) as 'recordDate' FROM t_borrow_apply n WHERE n.applyTime>= #{startRecordDate} AND n.applyTime<=#{endRecordDate} AND FIND_IN_SET(n.cityName,(SELECT allotCitys FROM t_base_cfg))
AND n.cityName = #{cityName}
GROUP BY n.cityName ) as t

SELECT CONCAT_WS('至',#{startDateStr},#{endDateStr}) as 'recordDate', t.channelCode, IFNULL(t2.sucBookCount,0) as 'sucBookCount', IFNULL(t3.totalSignCount,0) as 'totalSignCount', IFNULL(t4.sucRetCount,0) as 'sucRetCount', ROUND(IFNULL(t4.sucRetAmount,0),2) as 'sucRetAmount', ROUND(IFNULL(t5.sucSaleCount,0)-IFNULL(t6.failSaleCount,0),2) as 'sucSaleCount', ROUND(IFNULL(t5.sucSaleAmt,0)-IFNULL(t6.failSaleAmt,0),2) as 'sucSaleAmt' FROM ( SELECT n.channelCode FROM t_borrow_apply n LEFT JOIN t_borrow_channel t on n.channelCode = t.channelCode JOIN t_borrow_team t1 on t.teamNo = t1.teamNo WHERE
AND t1.teamNo = #{teamId}
GROUP BY n.channelCode ) as t LEFT JOIN ( SELECT n.channelCode, COUNT(DISTINCT m.applyId) as 'sucBookCount' FROM t_treat_visit_detail m JOIN t_borrow_apply n ON n.applyId = m.applyId WHERE m.visitTime>= #{startRecordDate} AND m.visitTime<=#{endRecordDate}
AND n.channelCode !=''
AND n.channelCode = #{channelCode}
GROUP BY n.channelCode ) t2 ON t2.channelCode = t.channelCode LEFT JOIN ( SELECT n.channelCode, COUNT(DISTINCT m.applyId) as 'totalSignCount' FROM t_treat_info m JOIN t_borrow_apply n ON n.applyId = m.applyId WHERE m.createTime>= #{startRecordDate} AND m.createTime<=#{endRecordDate} AND m.`status` in(1,2)
AND n.channelCode !=''
AND n.channelCode = #{channelCode}
GROUP BY n.channelCode ) t3 ON t3.channelCode = t.channelCode LEFT JOIN ( SELECT n.channelCode, COUNT(m.recordId) as 'sucRetCount' , IFNULL(SUM(m.feeAmount),0) AS 'sucRetAmount' FROM t_treat_success m JOIN t_borrow_apply n ON n.applyId = m.applyId WHERE m.feeAmountDate>= #{startRecordDate} AND m.feeAmountDate<=#{endRecordDate}
AND n.channelCode !=''
AND n.channelCode = #{channelCode}
AND m.`status`=2 GROUP BY n.channelCode ) t4 ON t4.channelCode = t.channelCode
LEFT JOIN( SELECT n.channelCode, COUNT(DISTINCT n.applyId) as 'sucSaleCount', SUM(n.costPrice*n.discount)/100 as 'sucSaleAmt' FROM t_borrow_sel_count n WHERE n.createTime>= #{startRecordDate} AND n.createTime<=#{endRecordDate}
AND n.channelCode !=''
AND n.channelCode = #{channelCode}
AND n.handleType=2 GROUP BY n.channelCode ) as t5 ON t5.channelCode = t.channelCode
LEFT JOIN( SELECT n.channelCode, COUNT(DISTINCT n.applyId) as 'failSaleCount', SUM(n.costPrice*n.discount)/100 as 'failSaleAmt' FROM t_borrow_sel_count n WHERE n.createTime>= #{startRecordDate} AND n.createTime<=#{endRecordDate}
AND n.channelCode !=''
AND n.channelCode = #{channelCode}
AND n.handleType=4 GROUP BY n.channelCode ) as t6 ON t6.channelCode = t.channelCode

SELECT COUNT(*) FROM ( SELECT n.channelCode FROM t_borrow_apply n LEFT JOIN t_borrow_channel t on n.channelCode = t.channelCode JOIN t_borrow_team t1 on t.teamNo = t1.teamNo WHERE
AND t1.teamNo = #{teamId}
GROUP BY n.channelCode ) as t

