mapper.select:
SELECT
	recordDate,
	orgId,
	cityName, 
	applyCount
FROM t_org_allot_record

	
	  and recordDate = #{recordDate}
	 
	
	  and orgId = #{orgId}
	 
	
		and cityName = #{cityName}
	
	
		and applyCount = #{applyCount}
	

 
	ORDER by ${orderSql}

SELECT COUNT(*) FROM t_org_allot_record 


    and recordDate = #{recordDate}
 

    and orgId = #{orgId}
 

	and cityName = #{cityName}


	and applyCount = #{applyCount}

mapper.insert:
INSERT INTO t_org_allot_record ( recordDate, cityName, orgId, applyCount ) VALUES ( #{recordDate, jdbcType=DATE,typeHandler=DateHandler}, #{cityName, jdbcType=VARCHAR}, #{orgId, jdbcType=INTEGER,typeHandler=NumberHandler},
#{applyCount, jdbcType=INTEGER,typeHandler=NumberHandler}
0
)

mapper.delete:
DELETE FROM t_org_allot_record 
 
	recordDate = #{recordDate} AND 
	orgId = #{orgId}

mapper.update:
UPDATE t_org_allot_record  

	
		cityName = #{cityName, jdbcType=VARCHAR},
	
	
		applyCount = applyCount + #{addApplyCount}
	


	  recordDate = #{recordDate} AND 
	  orgId = #{orgId}
