mapper.select:
SELECT
	applyId, 
	orgId,
	channelCode,
	dataType,
	`status`,
	customerId, 
	price, 
	DATE_FORMAT(createTime,'%Y-%m-%d %H:%i:%s') as createTime ,
	DATE_FORMAT(updateTime,'%Y-%m-%d %H:%i:%s') as updateTime   
FROM t_org_cost_record

	
	  and orgId = #{orgId}
	 
	
		and applyId = #{applyId}
	
	
		and customerId = #{customerId}
	
	
		and `status` = #{status}
	

 
	ORDER by ${orderSql}

SELECT COUNT(*) FROM t_org_cost_record 

	
	  and orgId = #{orgId}
	 
	
		and applyId = #{applyId}
	
	
		and customerId = #{customerId}
	
	
		and `status` = #{status}
