mapper.select:
SELECT
	applyId,
	customerId, 
	applyName, 
	applyCount, 
	telephone, 
	cityName, 
	channelCode, 
	channelDetail, 
	haveDetail, 
	status, 
	orderStatus, 
	lastStore, 
	recorder, 
	DATE_FORMAT(createTime,'%Y-%m-%d %H:%i:%s') as createTime,
	DATE_FORMAT(applyTime,'%Y-%m-%d %H:%i:%s') as applyTime,
	DATE_FORMAT(updateTime,'%Y-%m-%d %H:%i:%s') as updateTime,
	custLabel, 
	orderType, 
	orgId, 
	DATE_FORMAT(allotTime,'%Y-%m-%d %H:%i:%s') as allotTime,
	allotBy, 
	allotDesc, 
	grade,
	applyType,
	DATE_FORMAT(lastUpdateTime,'%Y-%m-%d %H:%i:%s') as lastUpdateTime,
	isHideFlag,
	backStatus,
	backDesc,
	backReDesc,
	muLoanStatus,
	orderRate,
	DATE_FORMAT(orderTypeUpdateTime,'%Y-%m-%d %H:%i:%s') as orderTypeUpdateTime,
	DATE_FORMAT(custLabelUpTime,'%Y-%m-%d %H:%i:%s') as custLabelUpTime
FROM t_borrow_store_apply

	
	  and applyId = #{applyId}
	 
	
		and customerId = #{customerId}
	
	
		and applyName = #{applyName}
	
	
		and applyCount = #{applyCount}
	
	
		and telephone = #{telephone}
	
	
		and cityName = #{cityName}
	
	
		and channelCode = #{channelCode}
	
	
		and channelDetail = #{channelDetail}
	
	
		and haveDetail = #{haveDetail}
	
	
		and status = #{status}
	
	
		and orderStatus = #{orderStatus}
	
	
		and orderStatus in(${inOrderStatus})
	
	
		and lastStore = #{lastStore}
	
	
		and recorder = #{recorder}
	
	
		and createTime = #{createTime}
	
	
		and applyTime = #{applyTime}
	
	
		and updateTime = #{updateTime}
	
	
		and custLabel = #{custLabel}
	
	
		and orderType = #{orderType}
	
	
		and orgId = #{orgId}
	
	
		and allotTime = #{allotTime}
	
	
		and allotBy = #{allotBy}
	
	
		and allotDesc = #{allotDesc}
	
	
		and grade = #{grade}
	
	
		and applyType = #{applyType}
	
	
		and lastUpdateTime = #{lastUpdateTime}
	
	
		and isHideFlag = #{isHideFlag}
	
	
		and backStatus = #{backStatus}
	
	
		and backDesc = #{backDesc}
	
	
		and backReDesc = #{backReDesc}
	
	
		and muLoanStatus = #{muLoanStatus}
	
	
		and dealOrderType = #{dealOrderType}
	
	
		and orderRate = #{orderRate}
	
	
		and orderTypeUpdateTime = #{orderTypeUpdateTime}
	
	
		and custLabelUpTime = #{custLabelUpTime}
	

 
	ORDER by ${orderSql}

SELECT COUNT(*) FROM t_borrow_store_apply 


    and applyId = #{applyId}
 

	and customerId = #{customerId}


	and applyName = #{applyName}


	and applyCount = #{applyCount}


	and telephone = #{telephone}


	and cityName = #{cityName}


	and channelCode = #{channelCode}


	and channelDetail = #{channelDetail}


	and haveDetail = #{haveDetail}


	and status = #{status}


	and orderStatus = #{orderStatus}


	and lastStore = #{lastStore}


	and recorder = #{recorder}


	and createTime = #{createTime}


	and applyTime = #{applyTime}


	and updateTime = #{updateTime}


	and custLabel = #{custLabel}


	and orderType = #{orderType}


	and orgId = #{orgId}


	and allotTime = #{allotTime}


	and allotBy = #{allotBy}


	and allotDesc = #{allotDesc}


	and grade = #{grade}


	and applyType = #{applyType}


	and lastUpdateTime = #{lastUpdateTime}


	and isHideFlag = #{isHideFlag}


	and backStatus = #{backStatus}


	and backDesc = #{backDesc}


	and DATE_FORMAT(allotTime,'%Y-%m-%d') = #{nowAllotDate}


	and backReDesc = #{backReDesc}


	and muLoanStatus = #{muLoanStatus}


	and dealOrderType = #{dealOrderType}


	and orderRate = #{orderRate}


	and orderTypeUpdateTime = #{orderTypeUpdateTime}


	and custLabelUpTime = #{custLabelUpTime}

SELECT t.applyName, t.status, (CASE t.`status` WHEN 0 THEN '待处理' WHEN 1 THEN '客服锁定中' WHEN 2 THEN '门店锁定中' WHEN 3 THEN '可转化' WHEN 4 THEN '转化中' WHEN 5 THEN '转化成功' WHEN 6 THEN '转化失败' WHEN 7 THEN '门店退回' WHEN 8 THEN '过期失效' ELSE '未知' END ) statusDesc, t.telephone, t.applyCount, t.applyType, IFNULL(t.orderStatus,0) orderStatus, t.channelDetail, DATE_FORMAT(t.applyTime,'%Y-%m-%d %H:%i:%s') as applyTime , t.orderType, t.lastStore as storeCustId FROM t_borrow_store_apply t
and t.applyId = #{applyId}
and t.telephone = #{telephone}
and ( t.telephone = #{checkTelephone} and t.applyId != #{checkApplyId} )

SELECT b.loanAmount, b.loanDeadline, b.loanPurpose, b.cityName, IFNULL(a.orderStatus,-1) as orderStatus, a.applyName, a.telephone, a.custLabel, DATE_FORMAT(a.applyTime,'%Y-%m-%d %H:%i:%s') as applyTime, b.`desc`, a.applyType, a.channelCode, a.channelDetail, a.custLabel, IFNULL(a.dealOrderType,-1) as dealOrderType, IFNULL(a.orderRate,0) as orderRate, a.applyId, c.`status` as bookStatus, d.channelCode, d.type, (CASE d.type WHEN 1 THEN '自有平台' WHEN 2 THEN '推广渠道' WHEN 3 THEN 'API接口' WHEN 4 THEN '历史数据' WHEN 5 THEN '推广渠道' WHEN 6 THEN '测试数据' ELSE 'API接口' END) as 'channelTypeText', DATE_FORMAT(a.lastUpdateTime,'%Y-%m-%d %H:%i:%s') as lastUpdateTime, (case when #{roleType} = 1 then a.telephone else null end ) as noHideMobile FROM T_BORROW_STORE_APPLY a LEFT JOIN T_BORROW_BASE b ON a.applyId = b.applyId LEFT JOIN t_treat_book c ON a.applyId = c.applyId LEFT JOIN t_borrow_channel d ON a.channelCode = d.channelCode
and a.applyId = #{applyId}
ORDER by ${orderSql}

mapper.sql:
AND t.allotTime < DATE_SUB(CURDATE(),INTERVAL 1 day)

	AND t.`lastStore` = #{lastStore}


	AND t.`allotTime` >= #{startAllotTime}

mapper.select:
SELECT t.orderStatus, (CASE t.orderStatus WHEN -1 THEN '待跟进' WHEN 0 THEN '跟进过' WHEN 1 THEN '未上门待签约' WHEN 2 THEN '已上门待签约' WHEN 3 THEN '已上门签约' WHEN 4 THEN '进件审核中' WHEN 5 THEN '银行已放款' WHEN 6 THEN '银行已拒绝' WHEN 7 THEN '无效客户' WHEN 8 THEN '空号/错号' END) as orderStatusText, DATE_FORMAT(t.applyTime,'%Y/%m/%d') as applyTime, t2.loanAmount, t2.loanDeadline, t1.realName, t1.telephone FROM t_borrow_store_apply t JOIN t_busi_cust t1 ON t1.customerId = t.lastStore LEFT JOIN t_borrow_base t2 ON t.applyId = t2.applyId
AND t.applyName = #{applyName}
AND t.telephone = #{telephone}
