mapper.select:
SELECT COUNT(*) FROM T_customer t

	
	  and t.customerId = #{customerId}
	 
	
		and t.userName = #{userName}
	
	
		and t.password = #{password}
	
	
		and t.referer = #{referer}

sql.where:
t.`status`=1 AND t.roleType = 2

	AND t1.isKfAllot = #{isKfAllot}


    AND ( t.userName like '${userName}%' OR t.telephone like '${userName}')
 

	AND t1.isAllotLend = #{isAllotLend}


	AND t2.onJob = #{onJob}


	AND t.kfAuthRole = #{kfAuthRole}


	 and t.registerTime >= #{startTime} 


	 and t.registerTime < DATE_ADD(#{endTime},INTERVAL 1 DAY)

mapper.select:
SELECT t1.orgId, t1.orgName, IFNULL(t2.realName,t.userName) as realName, t.customerId, t.telephone FROM t_customer t LEFT JOIN t_cust_identify t2 ON t.customerId = t2.customerId left JOIN t_org t1 ON t.orgId = t1.orgId
and t.customerId = #{customerId}
and t.telephone = #{telephone}
and t1.cityName like CONCAT('%',#{cityName},'%')
and (t2.realName like CONCAT('%',#{realName},'%') or t.userName like CONCAT('%',#{realName},'%'))
and t.roleType = #{roleType}
and t.roleType in(${inRoleType})
and t.`status` = #{status}

SELECT
	customerId,
	telephone 
FROM t_customer

	
	  and customerId = #{customerId}
	 
	
    	and telephone = #{telephone}
 	
 	
    	and roleType = #{roleType}
 	
 	
    	and status = #{status}
 	
 	

 
	ORDER by ${orderSql}

SELECT count(*)
FROM t_customer

	
	  and customerId = #{customerId}
	 
	
    	and telephone = #{telephone}

SELECT
	t.customerId,
	t.`status`
FROM t_customer t
LEFT JOIN t_cust_identify t2 ON t.customerId = t2.customerId

	
    	and t.telephone = #{userName}
 	
 	
		and t.customerId = #{customerId}
	
	
    	and t.password = #{password}
 	
 	
    	and t.telephone = #{telephone}
 	
	
    	and t2.cardNo = #{cardNo}
 	
 	
    	and t.openid = #{openid}
 	
 	
    	and t.unionid = #{unionid}

SELECT t.customerId, t.unionid, t.userName, t.telephone, t.email, t.sourceType, t.userType, t.vipGrade, DATE_FORMAT(t.vipEndDate,'%Y-%m-%d') as "vipEndDate", t.referer, t.`status`, t.remindStatus, t.platform, t.openid, t.gzhId, IFNULL(t.imName,' ') as imName, t.roleType, t5.orgId, t5.orgNo, t.authRole as authType, t.kfAuthRole as kfAuthType, t.userOrgs, t.groupName, t.teamName, t4.referer refererTwo, IFNULL(t1.headImgUrl,' ') as userImage, t1.auditDesc, t1.qrcodeImgUrl, t1.provice, t1.cityName, t1.cityArea, t1.address, t1.custDesc, IFNULL(t1.headStatus,-1) as 'headStatus', IFNULL(t2.realName,' ') as "realName", t2.cardNo, IFNULL(t2.`status`,-1) as 'identifyStatus', t3.lenderType, t3.company, t3.companyDesc, t3.compType, t3.compId, IFNULL(t3.`status`,-1) as 'cardStatus', DATE_FORMAT(t.registerTime,'%Y-%m-%d %H:%i:%s') as "registerTime", t5.orgFlag FROM t_customer t LEFT JOIN t_cust_info t1 ON t.customerId = t1.customerId LEFT JOIN t_cust_identify t2 ON t.customerId = t2.customerId LEFT JOIN t_cust_identify_card t3 on t.customerId = t3.customerId LEFT JOIN t_customer t4 on t4.customerId = t.referer LEFT JOIN t_org t5 ON t5.orgId = t.orgId LEFT JOIN t_sys_role t6 ON t6.roleId = t.authRole
and t.telephone = #{userName}
and t.customerId = #{customerId}
and t.password = #{password}
and t.telephone = #{telephone}
and t2.cardNo = #{cardNo}
and t.openid = #{openid}
and t.unionid = #{unionid}
limit 10

mapper.update:
UPDATE t_customer  
       set status = #{status, jdbcType=INTEGER,typeHandler=NumberHandler}
       where customerId = #{customerId}

mapper.select:
SELECT
	customerId,
	telephone,
	`status`,
	password,
	tradePwd
FROM t_customer

	
	  and customerId = #{customerId}
	 
	
    	and telephone = #{telephone}
 	
 	
    	and password = #{password}
 	
 	
    	and tradePwd = #{tradePwd}

select IFNULL(tab1.totalAmount,0) as totalAmount,IFNULL(tab2.totalScore,0) as totalScore,
	 IFNULL(tab3.totalPeople,0) as totalPeople
from t_customer c 
LEFT JOIN(
select t.customerId,SUM(t.amount)as totalAmount 
	from t_fund_record t where t.fundType='refererFirstRob' and t.customerId = #{customerId}
)tab1 ON c.customerId = tab1.customerId 
LEFT JOIN (
select cs.customerId,SUM(cs.score) as totalScore from t_cust_score cs 
	where (cs.createDesc='推荐人工作认证' or cs.createDesc='推荐人实名认证') and cs.customerId = #{customerId}
)tab2 ON tab2.customerId = c.customerId
LEFT JOIN (
select referer,COUNT(c.customerId)as totalPeople from t_customer c where c.referer = #{customerId}
)tab3 ON tab3.referer = c.customerId
where c.customerId = #{customerId}

SELECT t1.customerId as cid, t1.registerTime as rtime, t1.telephone as tel, t1.sourceType as crst, t1.appType, t2.updateTime AS xxjrlt, t3.updateTime AS xdjllt, t4.realName AS cname, t5.cityName AS ccity, TRUE as isr, now() as ctime FROM t_customer t1 LEFT JOIN t_cust_signid t2 ON t1.customerId = t2.customerId AND t2.signType = "appxxjr" LEFT JOIN t_cust_signid t3 ON t1.customerId = t3.customerId AND t3.signType = "appxdjl" LEFT JOIN t_cust_identify t4 ON t1.customerId = t4.customerId LEFT JOIN t_cust_info t5 ON t1.customerId = t5.customerId WHERE t1.telephone IS NOT NULL AND t1.telephone != ""
AND t1.customerId > ${customerId}
AND t1.customerId in (${inCustomerId})
ORDER by ${orderSql}
LIMIT ${limit}

SELECT DATE_FORMAT(t.registerTime, '%Y-%m-%d') recordDate, SUM(CASE WHEN t.userType = 1 THEN 1 ELSE 0 END) xdjlCount, SUM(CASE WHEN t.sourceType = 'xxjr' THEN 1 ELSE 0 END) xxjrCount, SUM(CASE WHEN t.sourceType = 'wx' THEN 1 ELSE 0 END) wxCount, SUM(CASE WHEN t.sourceType = 'Channel ID' THEN 1 ELSE 0 END) channelIdCount FROM t_customer t
t.roleType = 0
AND t.registerTime >= #{startRecordDate}
AND t.registerTime <= #{endRecordDate}
GROUP BY recordDate
ORDER by ${orderSql}

 SELECT COUNT(1) FROM (
	SELECT
		DATE_FORMAT(t.registerTime, '%Y-%m-%d') recordDate
	FROM
		t_customer t
	
      	t.roleType = 0
		
		  	 AND t.registerTime >= #{startRecordDate} 
		
		
		     AND t.registerTime <= #{endRecordDate}
		
	
	GROUP BY recordDate
) t2
