mapper.select:
SELECT 
	t.orgId,
	t.orgName,
	t.orgNo,
	t.province,
	t.cityName,
	t.address,
	t.dataCost,
	t.isNet,
	t.isCount,
	DATE_FORMAT(t.createTime,'%Y-%m-%d %H:%i:%s') as createTime,
	t.orgFlag
FROM
t_org t

	
		t.orgId = #{orgId}
	
	
		t.orgNo = #{orgNo}
	
	
		and t.orgName =#{orgName} 
	
	
		and t.cityName =#{cityName} 
	
	
		and t.orgFlag =#{orgFlag} 
	

 
	ORDER by ${orderSql}

	SELECT COUNT(*) FROM t_org t 
	
		
			t.orgId = #{orgId}
		
		
			and t.orgName =#{orgName} 
		
		
			and t.cityName =#{cityName} 
		
		
			and t.orgFlag =#{orgFlag}

SELECT t.orgId,t.orgName,t.orgNo,t.province,t.cityName,t.address,t.dataCost
FROM
t_org t

	
		t.orgId = #{orgId}
	
	
		and t.orgId in (${userOrgs})
	
	
	
		and t.orgName = #{orgName}

SELECT orgId, orgName, orgNo, dataCost, province, cityName, address, managerName, managerTel, orgType, isNet, isCount, DATE_FORMAT(createTime,'%Y-%m-%d %H:%i:%s') as createTime, orgFlag FROM t_org
and orgId = #{orgId}
and orgName = #{orgName}
and orgCode = #{orgCode}
ORDER by ${orderSql}

SELECT COUNT(*) FROM t_org 


  	and orgId = #{orgId}
 

	and orgName = #{orgName}


	and orgCode = #{orgCode}
